SELECT MIN(title.id), MIN(aka_title.movie_id), MIN(name.name), MIN(movie_companies.movie_id), MIN(movie_keyword.movie_id), MIN(title.title), MIN(cast_info.note), MIN(cast_info.movie_id) FROM title, movie_keyword, name, movie_companies, aka_title, cast_info WHERE title.title != '(#1.8)' AND name.name IN ('Stalzer, Chris', 'Jones, David', 'Alié, René', 'Smith, Chris', 'Smith, David', 'Zabaleta, Guillermo', 'Bosman, Dick', 'Johnson, Michael', 'Reyna, Antonio', 'Williams, John', 'Smith, Mike', 'Morgan, Alexandra', 'Cowen, Howie', 'Smith, Steve', 'Williams, David', 'Bonnard, Gilles', 'Liégeois, Jean-Louis', 'Smith, Michael', 'Williams, Michael', 'Henry, Cory') AND cast_info.note != '(associate producer)' AND title.id = aka_title.movie_id AND movie_companies.movie_id = title.id AND cast_info.movie_id = movie_keyword.movie_id;