SELECT MIN(title.id), MIN(movie_info.movie_id), MIN(movie_link.movie_id), MIN(aka_title.movie_id), MIN(movie_companies.company_id), MIN(movie_keyword.keyword_id), MIN(movie_info.note), MIN(keyword.id), MIN(company_name.id), MIN(company_name.country_code), MIN(keyword.keyword) FROM keyword, title, movie_keyword, movie_companies, movie_link, aka_title, company_name, movie_info WHERE keyword.keyword != 'covered-in-mud' AND company_name.country_code IN ('[ve]', '[mn]', '[fr]', '[au]', '[ee]', '[ca]', '[ki]', '[gb]', '[bm]', '[gl]', '[us]', '[kr]', '[in]', '[de]', '[bg]', '[es]', '[th]', '[it]', '[na]', '[jp]') AND movie_info.note != '(studio)' AND keyword.id = movie_keyword.keyword_id AND movie_info.movie_id = aka_title.movie_id AND movie_link.movie_id = movie_info.movie_id AND company_name.id = movie_companies.company_id AND movie_info.movie_id = title.id;