SELECT MIN(link_type.link), MIN(aka_name.person_id), MIN(person_info.person_id), MIN(title.episode_nr), MIN(complete_cast.subject_id), MIN(company_name.name), MIN(role_type.role), MIN(person_info.note), MIN(comp_cast_type.kind), MIN(name.name), MIN(aka_name.name), MIN(title.production_year), MIN(title.title), MIN(comp_cast_type.id), MIN(kind_type.kind), MIN(name.gender), MIN(title.id), MIN(cast_info.person_id), MIN(movie_companies.movie_id), MIN(info_type.info), MIN(cast_info.note), MIN(cast_info.movie_id), MIN(keyword.keyword), MIN(movie_keyword.movie_id), MIN(complete_cast.movie_id), MIN(company_name.country_code) FROM title, role_type, keyword, movie_keyword, name, kind_type, comp_cast_type, complete_cast, link_type, movie_companies, person_info, company_name, info_type, aka_name, cast_info WHERE name.name IS NULL AND title.production_year BETWEEN 1984 AND 2008 AND keyword.keyword = 'covered-in-mud' AND kind_type.kind IS NOT NULL AND aka_name.name IS NOT NULL AND comp_cast_type.kind IS NULL AND company_name.country_code IS NULL AND title.title IS NOT NULL AND cast_info.note IS NULL AND title.episode_nr IS NOT NULL AND company_name.name = 'Walt Disney Studios Motion Pictures' AND info_type.info != 'where now' AND person_info.note = 'Steve Shelokhonov' AND role_type.role IN ('production designer', 'guest', 'actor', 'composer', 'actress', 'writer', 'director', 'costume designer', 'producer', 'cinematographer', 'miscellaneous crew', 'editor') AND name.gender IS NOT NULL AND link_type.link IN ('features', 'referenced in', 'spin off', 'follows', 'edited from', 'alternate language version of', 'references', 'version of', 'remake of', 'featured in', 'spoofs', 'similar to', 'followed by', 'spoofed in', 'edited into', 'unknown link', 'spin off from', 'remade as') AND title.id = complete_cast.movie_id AND cast_info.movie_id = movie_keyword.movie_id AND movie_companies.movie_id = cast_info.movie_id AND cast_info.person_id = aka_name.person_id AND person_info.person_id = aka_name.person_id AND comp_cast_type.id = complete_cast.subject_id;