SELECT MIN(cast_info.person_id), MIN(name.name), MIN(aka_name.person_id), MIN(name.id), MIN(person_info.person_id), MIN(company_name.name), MIN(name.gender) FROM name, person_info, company_name, aka_name, cast_info WHERE name.name = 'Williams, John' AND name.gender != '' AND company_name.name IS NOT NULL AND name.id = cast_info.person_id AND name.id = aka_name.person_id AND name.id = person_info.person_id;