SELECT MIN(comp_cast_type.kind), MIN(movie_companies.company_type_id), MIN(movie_companies.movie_id), MIN(company_type.id), MIN(movie_keyword.movie_id), MIN(movie_companies.note) FROM movie_keyword, movie_companies, company_type, comp_cast_type WHERE comp_cast_type.kind IN ('crew', 'complete+verified', 'cast', 'complete') AND movie_companies.note IS NULL AND company_type.id = movie_companies.company_type_id AND movie_companies.movie_id = movie_keyword.movie_id;