SELECT MIN(company_type.kind), MIN(movie_info.movie_id), MIN(comp_cast_type.kind), MIN(complete_cast.status_id), MIN(movie_info.note), MIN(comp_cast_type.id), MIN(complete_cast.movie_id), MIN(keyword.keyword) FROM keyword, comp_cast_type, complete_cast, movie_info, company_type WHERE movie_info.note IS NOT NULL AND company_type.kind IS NULL AND comp_cast_type.kind = 'complete+verified' AND keyword.keyword = 'covered-in-mud' AND movie_info.movie_id = complete_cast.movie_id AND comp_cast_type.id = complete_cast.status_id;