SELECT MIN(link_type.link), MIN(link_type.id), MIN(cast_info.person_id), MIN(movie_link.link_type_id), MIN(aka_name.name), MIN(aka_name.person_id), MIN(name.id), MIN(person_info.person_id), MIN(name.gender) FROM name, link_type, movie_link, person_info, aka_name, cast_info WHERE aka_name.name IS NOT NULL AND name.gender != 'm' AND link_type.link != 'spin off from' AND name.id = cast_info.person_id AND name.id = person_info.person_id AND cast_info.person_id = aka_name.person_id AND link_type.id = movie_link.link_type_id;