SELECT MIN(cast_info.movie_id), MIN(movie_info.movie_id), MIN(movie_companies.company_id), MIN(movie_companies.movie_id), MIN(movie_keyword.movie_id), MIN(movie_info.info), MIN(complete_cast.movie_id), MIN(company_name.id), MIN(company_name.name) FROM movie_keyword, complete_cast, movie_companies, company_name, movie_info, cast_info WHERE company_name.name != 'United International Pictures (UIP)' AND movie_info.info = 'When Matt buys Patt a new face for their anniversary, Patt reluctantly tries it on for size. But at a disastrous dinner with friends it becomes painfully evident to everyone that when it comes to faces, one size definitely does not fit all.' AND movie_info.movie_id = cast_info.movie_id AND company_name.id = movie_companies.company_id AND movie_info.movie_id = movie_companies.movie_id AND movie_info.movie_id = complete_cast.movie_id AND movie_keyword.movie_id = movie_info.movie_id;