SELECT MIN(movie_info_idx.info), MIN(title.id), MIN(movie_info_idx.info_type_id), MIN(title.kind_id), MIN(kind_type.id), MIN(info_type.id), MIN(name.name_pcode_cf), MIN(kind_type.kind), MIN(movie_info_idx.movie_id), MIN(name.gender) FROM title, name, kind_type, movie_info_idx, info_type WHERE name.gender != '' AND kind_type.kind IN ('video game', 'episode', 'tv movie', 'tv mini series', 'video movie', 'tv series', 'movie') AND name.name_pcode_cf != 'Y6415' AND movie_info_idx.info IS NOT NULL AND info_type.id = movie_info_idx.info_type_id AND kind_type.id = title.kind_id AND movie_info_idx.movie_id = title.id;