SELECT MIN(title.id), MIN(company_type.kind), MIN(movie_link.movie_id), MIN(movie_companies.company_type_id), MIN(movie_companies.movie_id), MIN(movie_keyword.movie_id), MIN(company_type.id), MIN(title.production_year), MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(movie_link.linked_movie_id) FROM title, movie_keyword, company_type, complete_cast, movie_companies, movie_link, cast_info WHERE title.production_year = 1931 AND company_type.kind = 'production companies' AND title.id = complete_cast.movie_id AND company_type.id = movie_companies.company_type_id AND title.id = movie_link.movie_id AND movie_companies.movie_id = title.id AND title.id = movie_keyword.movie_id AND title.id = movie_link.linked_movie_id AND title.id = cast_info.movie_id;