SELECT MIN(role_type.role), MIN(link_type.link), MIN(title.id), MIN(title.kind_id), MIN(person_info.note), MIN(movie_info.movie_id), MIN(movie_link.movie_id), MIN(kind_type.id), MIN(movie_info.note), MIN(movie_companies.movie_id), MIN(title.production_year), MIN(movie_info.info), MIN(info_type.id), MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(movie_link.linked_movie_id), MIN(person_info.info_type_id) FROM title, role_type, kind_type, complete_cast, link_type, movie_companies, movie_link, person_info, info_type, movie_info, cast_info WHERE link_type.link IN ('edited from', 'version of', 'remade as', 'spin off from', 'features', 'spoofs', 'featured in', 'unknown link', 'alternate language version of', 'followed by', 'similar to', 'spoofed in', 'spin off', 'remake of', 'follows', 'edited into', 'references', 'referenced in') AND movie_info.info = 'Short' AND person_info.note IS NOT NULL AND title.production_year = 2015 AND movie_info.note != '(No. 41246)' AND role_type.role = 'composer' AND movie_link.movie_id = movie_info.movie_id AND movie_info.movie_id = movie_companies.movie_id AND movie_info.movie_id = title.id AND title.id = cast_info.movie_id AND title.id = movie_link.linked_movie_id AND movie_info.movie_id = complete_cast.movie_id AND info_type.id = person_info.info_type_id AND movie_companies.movie_id = title.id AND title.id = movie_link.movie_id AND movie_info.movie_id = cast_info.movie_id AND kind_type.id = title.kind_id;