SELECT MIN(cast_info.movie_id), MIN(title.id), MIN(movie_info.note), MIN(movie_info.movie_id), MIN(movie_info.info_type_id), MIN(info_type.id), MIN(role_type.role) FROM movie_info, cast_info, role_type, info_type, title WHERE movie_info.note IS NOT NULL AND role_type.role IS NOT NULL AND movie_info.movie_id = title.id AND info_type.id = movie_info.info_type_id AND movie_info.movie_id = cast_info.movie_id;