SELECT MIN(aka_title.movie_id), MIN(company_type.kind), MIN(movie_info.note), MIN(title.id), MIN(movie_info.movie_id), MIN(movie_info_idx.info_type_id), MIN(movie_link.movie_id), MIN(movie_info_idx.movie_id), MIN(person_info.info_type_id), MIN(movie_companies.movie_id), MIN(role_type.id), MIN(info_type.info), MIN(cast_info.role_id), MIN(info_type.id), MIN(role_type.role) FROM movie_link, company_type, movie_info, movie_companies, person_info, cast_info, role_type, movie_info_idx, aka_title, info_type, title WHERE movie_info.note = '(Finland: 2002)' AND info_type.info IN ('trivia', 'gross', 'certificates', 'LD original title', 'LD quality program', 'votes distribution', 'where now', 'production dates', 'LD contrast', 'pictorial', 'LD spaciality', 'LD video quality', 'LD sharpness', 'color info', 'LD year', 'opening weekend', 'LD laserdisc title', 'LD audio noise', 'runtimes', 'screenplay-teleplay') AND company_type.kind IN ('production companies', 'distributors', 'special effects companies', 'miscellaneous companies') AND role_type.role IN ('production designer', 'composer', 'actor', 'miscellaneous crew', 'cinematographer', 'guest', 'director', 'actress', 'costume designer', 'editor', 'producer', 'writer') AND movie_info.movie_id = aka_title.movie_id AND role_type.id = cast_info.role_id AND info_type.id = movie_info_idx.info_type_id AND movie_link.movie_id = movie_info.movie_id AND movie_info.movie_id = movie_companies.movie_id AND movie_info.movie_id = movie_info_idx.movie_id AND movie_info.movie_id = title.id AND info_type.id = person_info.info_type_id;