SELECT MIN(link_type.link), MIN(person_info.note), MIN(link_type.id), MIN(cast_info.person_id), MIN(movie_link.link_type_id), MIN(name.id), MIN(person_info.person_id) FROM name, link_type, person_info, movie_link, cast_info WHERE person_info.note IS NOT NULL AND link_type.link IS NOT NULL AND name.id = person_info.person_id AND link_type.id = movie_link.link_type_id AND cast_info.person_id = person_info.person_id;