SELECT MIN(role_type.role), MIN(title.id), MIN(movie_info_idx.info_type_id), MIN(role_type.id), MIN(cast_info.role_id), MIN(movie_companies.movie_id), MIN(movie_keyword.movie_id), MIN(info_type.id), MIN(cast_info.movie_id), MIN(movie_info_idx.movie_id), MIN(movie_info_idx.info) FROM title, role_type, movie_keyword, movie_info_idx, movie_companies, info_type, cast_info WHERE movie_info_idx.info != '8' AND role_type.role != 'cinematographer' AND role_type.id = cast_info.role_id AND movie_info_idx.movie_id = movie_keyword.movie_id AND movie_info_idx.movie_id = movie_companies.movie_id AND movie_info_idx.movie_id = title.id AND cast_info.movie_id = movie_info_idx.movie_id AND info_type.id = movie_info_idx.info_type_id;