SELECT MIN(link_type.link), MIN(company_type.kind), MIN(cast_info.person_id), MIN(aka_name.name), MIN(aka_name.person_id), MIN(name.id), MIN(name.name_pcode_cf), MIN(company_name.country_code) FROM name, cast_info, link_type, company_name, aka_name, company_type WHERE link_type.link IN ('similar to', 'references', 'remade as', 'followed by', 'spoofed in', 'unknown link', 'version of', 'alternate language version of', 'spin off', 'referenced in', 'edited into', 'features', 'featured in', 'follows', 'spoofs', 'edited from', 'remake of', 'spin off from') AND company_type.kind != 'special effects companies' AND company_name.country_code != '[it]' AND name.name_pcode_cf != 'P6252' AND aka_name.name IN ('Jay', 'Joe', 'Chris', 'Mike', 'Callaghan, Leonard James', 'Russell, Silky', 'Miller, Professor Merton', 'Sam', 'Jimmy', 'Dilip, K.', 'Gaggiano, Roberto', 'Bob', 'Tony', 'credits, Hironori Kanno in English', 'Alex', 'Strait, George Harvey', 'Saldierna, Rechina', 'Medlin, Matt', 'Rambo Sambo', 'Bill') AND name.id = aka_name.person_id AND cast_info.person_id = aka_name.person_id;