SELECT MIN(title.id), MIN(cast_info.person_id), MIN(movie_companies.movie_id), MIN(name.id), MIN(cast_info.note), MIN(cast_info.movie_id), MIN(movie_info_idx.movie_id) FROM title, name, movie_info_idx, movie_companies, cast_info WHERE cast_info.note IS NOT NULL AND name.id = cast_info.person_id AND cast_info.movie_id = movie_info_idx.movie_id AND movie_companies.movie_id = cast_info.movie_id AND title.id = cast_info.movie_id;