SELECT MIN(movie_keyword.movie_id), MIN(info_type.info), MIN(cast_info.note), MIN(cast_info.movie_id), MIN(movie_info_idx.movie_id) FROM info_type, movie_keyword, cast_info, movie_info_idx WHERE cast_info.note IN ('(production coordinator)', '(script supervisor) (as Schno Mozingo)', '(executive producer)', '(writer)', '(producer) (as Koh Shibusawa)', '(associate producer)', '(written by)', '(as Bulgari)', '(accountant: TFC Trickompany)', '(as Rosa Frausto)', '(voice)', '(creator)', '(co-executive producer) (as Tron Hauge)', '(co-executive producer) (as Lyn Greene)', '(production assistant)', '(uncredited)', '(as Joe Pazos)', '(production financing) (as Christl Bucina)', '(as Gustave Von Seyffertitz)', '(producer)') AND info_type.info != 'LD video quality' AND cast_info.movie_id = movie_keyword.movie_id AND cast_info.movie_id = movie_info_idx.movie_id;