SELECT MIN(title.id), MIN(company_type.kind), MIN(comp_cast_type.kind), MIN(complete_cast.status_id), MIN(name.name), MIN(movie_keyword.movie_id), MIN(comp_cast_type.id), MIN(kind_type.kind), MIN(title.episode_nr), MIN(company_name.country_code) FROM title, movie_keyword, name, kind_type, comp_cast_type, complete_cast, company_name, company_type WHERE company_type.kind IS NOT NULL AND company_name.country_code IS NULL AND title.episode_nr NOT BETWEEN 80248 AND 84795 AND comp_cast_type.kind IS NULL AND kind_type.kind IN ('tv series', 'tv movie', 'episode', 'movie', 'video movie', 'video game', 'tv mini series') AND name.name = 'Smith, Mike' AND title.id = movie_keyword.movie_id AND comp_cast_type.id = complete_cast.status_id;