SELECT MIN(name.name_pcode_cf), MIN(person_info.person_id), MIN(company_name.name), MIN(movie_info_idx.info), MIN(role_type.role), MIN(person_info.note), MIN(name.name), MIN(aka_name.name), MIN(title.production_year), MIN(title.title), MIN(name.id), MIN(kind_type.kind), MIN(movie_companies.note), MIN(name.gender), MIN(company_type.kind), MIN(kind_type.id), MIN(movie_companies.movie_id), MIN(info_type.info), MIN(cast_info.note), MIN(keyword.keyword), MIN(title.kind_id), MIN(movie_keyword.movie_id), MIN(movie_info.info), MIN(char_name.name), MIN(company_name.country_code) FROM title, role_type, keyword, movie_info, name, kind_type, movie_keyword, movie_info_idx, cast_info, char_name, movie_companies, person_info, company_name, info_type, aka_name, company_type WHERE kind_type.kind = 'tv series' AND company_name.country_code IN ('[gl]', '[gb]', '[in]', '[ki]', '[jp]', '[th]', '[ca]', '[ee]', '[us]', '[fr]', '[mn]', '[bg]', '[it]', '[na]', '[ve]', '[kr]', '[au]', '[es]', '[de]', '[bm]') AND name.name_pcode_cf != 'J5252' AND cast_info.note IS NULL AND keyword.keyword IS NULL AND movie_info_idx.info != '01..0003.2' AND company_type.kind IS NULL AND role_type.role = 'costume designer' AND char_name.name IS NOT NULL AND info_type.info IS NULL AND name.name != 'Williams, John' AND title.production_year >= 1996 AND title.title != '(#1.9)' AND person_info.note != 'New Cinema Productions' AND movie_info.info = 'The hovercraft chase sequence was filmed nearby to a working airport. Pilots are understandably nervous about seeing gunfire and explosions at an airport so a schedule had to be worked out whereby filming could take place whenever the airport wasn''t too busy.' AND company_name.name IS NULL AND movie_companies.note != '(2006) (Slovenia) (DVD)' AND aka_name.name != 'Joe' AND name.gender != '' AND movie_companies.movie_id = movie_keyword.movie_id AND kind_type.id = title.kind_id AND name.id = person_info.person_id;