SELECT MIN(company_type.kind), MIN(name.name), MIN(movie_info.note), MIN(aka_name.name), MIN(title.production_year), MIN(title.episode_nr), MIN(name.gender) FROM title, movie_info, name, aka_name, company_type WHERE name.gender IS NOT NULL AND title.production_year >= 1964 AND name.name IS NULL AND title.episode_nr >= 4946 AND movie_info.note = '(including commercials)' AND company_type.kind IN ('special effects companies', 'production companies', 'distributors', 'miscellaneous companies') AND aka_name.name != 'Gaggiano, Roberto';