SELECT MIN(name.name_pcode_cf), MIN(person_info.person_id), MIN(movie_info.note), MIN(movie_info_idx.info), MIN(aka_name.name), MIN(movie_link.linked_movie_id), MIN(movie_info_idx.movie_id), MIN(movie_companies.note), MIN(movie_companies.movie_id), MIN(role_type.role), MIN(aka_name.person_id) FROM movie_link, movie_companies, movie_info, aka_name, person_info, role_type, name, movie_info_idx WHERE aka_name.name = 'credits, Hironori Kanno in English' AND movie_info_idx.info = '......612.' AND role_type.role IN ('actor', 'production designer', 'writer', 'miscellaneous crew', 'director', 'producer', 'costume designer', 'editor', 'guest', 'actress', 'composer', 'cinematographer') AND name.name_pcode_cf IN ('I326', 'B6525', 'S3152', 'D3453', 'A5362', 'H6514', 'Q1323', 'Y6415', 'A5315', 'S5351', 'G5242', 'B341', 'D5254', 'P3625', 'J5252', 'R1632', 'M6352', 'P6252', 'T5125', 'O6254') AND movie_companies.note IS NOT NULL AND movie_info.note != '(f) (rating 1996)' AND movie_link.linked_movie_id = movie_companies.movie_id AND movie_info_idx.movie_id = movie_companies.movie_id AND person_info.person_id = aka_name.person_id;