SELECT MIN(title.kind_id), MIN(movie_info_idx.info_type_id), MIN(kind_type.kind), MIN(person_info.info_type_id), MIN(info_type.info), MIN(info_type.id), MIN(kind_type.id) FROM kind_type, person_info, movie_info_idx, info_type, title WHERE kind_type.kind != 'tv series' AND info_type.info = 'votes distribution' AND kind_type.id = title.kind_id AND info_type.id = person_info.info_type_id AND info_type.id = movie_info_idx.info_type_id;