SELECT MIN(cast_info.movie_id), MIN(title.id), MIN(title.episode_nr), MIN(movie_info_idx.movie_id), MIN(company_name.name), MIN(role_type.role) FROM company_name, cast_info, role_type, movie_info_idx, title WHERE title.episode_nr BETWEEN 88946 AND 91584 AND company_name.name = 'Nickelodeon' AND role_type.role IN ('writer', 'cinematographer', 'miscellaneous crew', 'actress', 'composer', 'costume designer', 'actor', 'producer', 'editor', 'guest', 'director', 'production designer') AND title.id = cast_info.movie_id AND movie_info_idx.movie_id = title.id;