SELECT MIN(person_info.person_id), MIN(cast_info.note), MIN(cast_info.person_role_id), MIN(role_type.id), MIN(cast_info.role_id), MIN(char_name.id), MIN(cast_info.person_id) FROM person_info, cast_info, char_name, role_type WHERE cast_info.note = '(as Bulgari)' AND role_type.id = cast_info.role_id AND cast_info.person_id = person_info.person_id AND char_name.id = cast_info.person_role_id;