SELECT MIN(movie_companies.company_id), MIN(movie_info.note), MIN(movie_info_idx.info_type_id), MIN(movie_info.movie_id), MIN(movie_link.movie_id), MIN(company_name.name), MIN(info_type.info), MIN(movie_keyword.movie_id), MIN(info_type.id), MIN(role_type.role), MIN(company_name.id) FROM movie_link, company_name, movie_info, movie_companies, role_type, movie_info_idx, movie_keyword, info_type WHERE role_type.role = 'writer' AND info_type.info IN ('opening weekend', 'LD video quality', 'LD year', 'color info', 'trivia', 'screenplay-teleplay', 'LD quality program', 'LD audio noise', 'where now', 'pictorial', 'LD contrast', 'LD laserdisc title', 'LD spaciality', 'LD original title', 'votes distribution', 'LD sharpness', 'runtimes', 'gross', 'production dates', 'certificates') AND company_name.name = '20th Century Fox' AND movie_info.note IN ('Kevlar01', 'Joseph Hollabaugh and HollabaughFilms', '(1 reel)', '(video premiere)', 'Anonymous', '(f) (rating 1996)', '(Finland: 2002)', 'Stacy Teixeira', '(including commercials)', '(TV premiere)', '(limited)', '(Getty Center)', '(certificate #37629)', '(No. 41246)', 'KGF Vissers', '(New York City, New York)', '(DVD premiere)', '(featured on Unforgiven DVD)', '(studio)', '(certificate #30342)') AND company_name.id = movie_companies.company_id AND movie_link.movie_id = movie_info.movie_id AND movie_keyword.movie_id = movie_info.movie_id AND info_type.id = movie_info_idx.info_type_id;