SELECT MIN(complete_cast.movie_id), MIN(company_type.id), MIN(company_type.kind), MIN(name.gender), MIN(title.id), MIN(movie_link.movie_id), MIN(movie_companies.note), MIN(movie_companies.movie_id), MIN(company_name.name), MIN(name.id), MIN(movie_companies.company_type_id), MIN(cast_info.person_id) FROM movie_link, complete_cast, movie_companies, company_type, company_name, cast_info, name, title WHERE name.gender != '' AND company_type.kind = 'distributors' AND movie_companies.note IS NULL AND company_name.name IN ('Westofi-Tonfilm-Productions', 'Overnight Productions', 'Nickelodeon', 'Universal Pictures International (UPI)', 'Sony Entertainment Television', 'United International Pictures (UIP)', 'H.I.S.', 'Sony Pictures Releasing', 'Reel DVD', 'AXN', 'Bae Yong-Kyun Productions', 'Trends Media Group', 'Walt Disney Studios Motion Pictures', 'No Fear Here Productions', 'Buena Vista International', 'DOD Entertainment', 'Atmospheric Pictures', '20th Century Fox', 'Flying Rhino Productions', 'Warner Bros.') AND company_type.id = movie_companies.company_type_id AND movie_companies.movie_id = complete_cast.movie_id AND movie_link.movie_id = movie_companies.movie_id AND movie_companies.movie_id = title.id AND name.id = cast_info.person_id;