SELECT MIN(company_type.id), MIN(company_type.kind), MIN(name.gender), MIN(person_info.person_id), MIN(movie_companies.company_type_id), MIN(name.id), MIN(cast_info.person_id), MIN(aka_name.person_id) FROM movie_companies, company_type, aka_name, person_info, cast_info, name WHERE company_type.kind IS NULL AND name.gender IS NULL AND name.id = cast_info.person_id AND name.id = aka_name.person_id AND name.id = person_info.person_id AND company_type.id = movie_companies.company_type_id;