SELECT MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(title.id), MIN(movie_info.movie_id), MIN(title.production_year), MIN(movie_info_idx.movie_id), MIN(movie_companies.movie_id), MIN(movie_keyword.movie_id) FROM complete_cast, movie_companies, movie_info, cast_info, movie_info_idx, movie_keyword, title WHERE title.production_year IN (1972) AND movie_info.movie_id = title.id AND title.id = complete_cast.movie_id AND title.id = cast_info.movie_id AND movie_info_idx.movie_id = title.id AND title.id = movie_keyword.movie_id AND movie_companies.movie_id = title.id;