SELECT MIN(comp_cast_type.kind), MIN(title.id), MIN(link_type.link), MIN(cast_info.note), MIN(title.episode_nr), MIN(movie_link.linked_movie_id), MIN(person_info.person_id), MIN(movie_info_idx.movie_id), MIN(company_name.name), MIN(cast_info.person_id) FROM movie_link, company_name, person_info, cast_info, movie_info_idx, comp_cast_type, link_type, title WHERE title.episode_nr <= 31482 AND company_name.name != 'Buena Vista International' AND comp_cast_type.kind IS NOT NULL AND cast_info.note IN ('(associate producer)', '(producer)', '(writer)', '(production assistant)', '(as Bulgari)', '(script supervisor) (as Schno Mozingo)', '(uncredited)', '(production coordinator)', '(producer) (as Koh Shibusawa)', '(creator)', '(accountant: TFC Trickompany)', '(as Joe Pazos)', '(written by)', '(co-executive producer) (as Lyn Greene)', '(voice)', '(as Rosa Frausto)', '(as Gustave Von Seyffertitz)', '(executive producer)', '(co-executive producer) (as Tron Hauge)', '(production financing) (as Christl Bucina)') AND link_type.link = 'edited into' AND movie_info_idx.movie_id = title.id AND cast_info.person_id = person_info.person_id AND title.id = movie_link.linked_movie_id;