SELECT MIN(link_type.link), MIN(movie_info.info_type_id), MIN(person_info.info_type_id), MIN(company_name.name), MIN(info_type.info), MIN(info_type.id) FROM company_name, movie_info, person_info, link_type, info_type WHERE company_name.name != 'Sony Entertainment Television' AND info_type.info != 'screenplay-teleplay' AND link_type.link != 'edited into' AND info_type.id = movie_info.info_type_id AND info_type.id = person_info.info_type_id;