SELECT MIN(title.id), MIN(movie_keyword.movie_id), MIN(title.production_year), MIN(info_type.info) FROM movie_keyword, info_type, title WHERE info_type.info IN ('gross', 'trivia', 'LD laserdisc title', 'certificates', 'LD spaciality', 'where now', 'LD sharpness', 'votes distribution', 'opening weekend', 'LD audio noise', 'runtimes', 'LD contrast', 'LD original title', 'screenplay-teleplay', 'LD quality program', 'color info', 'LD year', 'production dates', 'pictorial', 'LD video quality') AND title.production_year >= 1965 AND title.id = movie_keyword.movie_id;