SELECT MIN(name.name_pcode_cf), MIN(name.gender), MIN(title.title), MIN(aka_name.name), MIN(title.production_year), MIN(person_info.note), MIN(name.name), MIN(info_type.info) FROM aka_name, person_info, name, info_type, title WHERE name.name_pcode_cf IS NULL AND title.production_year <= 2005 AND title.title != '(#1.4)' AND name.gender IS NULL AND name.name IS NOT NULL AND person_info.note IS NOT NULL AND info_type.info != 'where now' AND aka_name.name IS NULL;