SELECT MIN(aka_title.movie_id), MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(title.id), MIN(link_type.link), MIN(title.episode_nr), MIN(movie_info.movie_id), MIN(movie_link.linked_movie_id), MIN(title.production_year), MIN(title.kind_id), MIN(movie_link.movie_id), MIN(movie_info_idx.movie_id), MIN(movie_companies.movie_id), MIN(kind_type.kind), MIN(kind_type.id) FROM movie_link, complete_cast, kind_type, movie_companies, movie_info, cast_info, movie_info_idx, aka_title, link_type, title WHERE title.episode_nr != 1740 AND title.production_year <= 2003 AND kind_type.kind IN ('movie', 'episode', 'tv mini series', 'video movie', 'video game', 'tv movie', 'tv series') AND link_type.link = 'remake of' AND title.id = cast_info.movie_id AND kind_type.id = title.kind_id AND title.id = movie_link.linked_movie_id AND title.id = movie_link.movie_id AND title.id = aka_title.movie_id AND movie_info_idx.movie_id = title.id AND title.id = complete_cast.movie_id AND movie_info.movie_id = title.id AND movie_companies.movie_id = title.id;