SELECT MIN(name.name_pcode_cf), MIN(movie_info.info), MIN(person_info.person_id), MIN(company_name.country_code), MIN(movie_info.movie_id), MIN(aka_name.name), MIN(movie_link.movie_id), MIN(name.id), MIN(kind_type.kind), MIN(role_type.role) FROM movie_link, kind_type, company_name, movie_info, aka_name, person_info, name, role_type WHERE aka_name.name IN ('credits, Hironori Kanno in English', 'Jay', 'Saldierna, Rechina', 'Russell, Silky', 'Alex', 'Medlin, Matt', 'Bill', 'Dilip, K.', 'Joe', 'Miller, Professor Merton', 'Chris', 'Tony', 'Strait, George Harvey', 'Bob', 'Sam', 'Gaggiano, Roberto', 'Rambo Sambo', 'Callaghan, Leonard James', 'Jimmy', 'Mike') AND name.name_pcode_cf != 'D3453' AND role_type.role IS NOT NULL AND company_name.country_code != '[es]' AND movie_info.info = 'English' AND kind_type.kind = 'tv mini series' AND name.id = person_info.person_id AND movie_link.movie_id = movie_info.movie_id;