SELECT MIN(movie_info_idx.info), MIN(role_type.id), MIN(cast_info.role_id), MIN(movie_info_idx.movie_id), MIN(name.name), MIN(info_type.info), MIN(movie_companies.movie_id), MIN(role_type.role) FROM movie_companies, cast_info, name, role_type, movie_info_idx, info_type WHERE role_type.role = 'writer' AND movie_info_idx.info IS NULL AND info_type.info = 'opening weekend' AND name.name IS NOT NULL AND movie_info_idx.movie_id = movie_companies.movie_id AND role_type.id = cast_info.role_id;