SELECT MIN(name.name_pcode_cf), MIN(comp_cast_type.kind), MIN(comp_cast_type.id), MIN(name.id), MIN(complete_cast.subject_id), MIN(aka_name.person_id), MIN(keyword.keyword) FROM aka_name, name, comp_cast_type, keyword, complete_cast WHERE name.name_pcode_cf = 'P6252' AND comp_cast_type.kind IS NOT NULL AND keyword.keyword IS NOT NULL AND name.id = aka_name.person_id AND comp_cast_type.id = complete_cast.subject_id;