SELECT MIN(person_info.note), MIN(title.title), MIN(company_name.name) FROM person_info, title, company_name WHERE person_info.note = 'Simone North' AND title.title IN ('(#1.8)', 'The Boardroom', '51º edición de los premios Ondas', 'Star Power', '(#2.127)', '(#1.2)', '(#1.10)', 'Zweedse rapsodie', '(#1.6)', 'A Visita da Velha Senhora', 'Song 25', '(#1.5)', 'Shipshape-Less', 'Eine Allerweltsgeschichte', '(#1.9)', '(#1.7)', 'El Señor Presidente', '(#1.4)', '(#1.1)', '(#1.3)') AND company_name.name = 'United International Pictures (UIP)';