SELECT MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(title.id), MIN(title.episode_nr), MIN(movie_info_idx.info), MIN(movie_link.movie_id), MIN(movie_info_idx.movie_id) FROM movie_link, cast_info, movie_info_idx, complete_cast, title WHERE title.episode_nr >= 37028 AND movie_info_idx.info = '9' AND title.id = movie_link.movie_id AND movie_info_idx.movie_id = title.id AND title.id = complete_cast.movie_id AND title.id = cast_info.movie_id;