SELECT MIN(aka_title.movie_id), MIN(comp_cast_type.kind), MIN(movie_info.info), MIN(link_type.link), MIN(movie_info_idx.info_type_id), MIN(movie_info.movie_id), MIN(aka_name.name), MIN(movie_link.movie_id), MIN(movie_companies.note), MIN(movie_companies.movie_id), MIN(name.name), MIN(info_type.info), MIN(movie_keyword.movie_id), MIN(info_type.id), MIN(cast_info.person_id), MIN(aka_name.person_id) FROM movie_link, movie_companies, movie_info, aka_name, cast_info, name, movie_info_idx, movie_keyword, aka_title, comp_cast_type, link_type, info_type WHERE movie_companies.note IS NULL AND name.name != 'Bonnard, Gilles' AND comp_cast_type.kind IS NOT NULL AND link_type.link IS NULL AND movie_info.info = 'Color' AND aka_name.name = 'Alex' AND info_type.info IN ('LD quality program', 'LD year', 'LD contrast', 'certificates', 'color info', 'trivia', 'votes distribution', 'LD video quality', 'LD spaciality', 'LD laserdisc title', 'production dates', 'runtimes', 'screenplay-teleplay', 'opening weekend', 'LD original title', 'pictorial', 'LD audio noise', 'gross', 'LD sharpness', 'where now') AND movie_companies.movie_id = aka_title.movie_id AND cast_info.person_id = aka_name.person_id AND movie_companies.movie_id = movie_keyword.movie_id AND movie_info.movie_id = movie_companies.movie_id AND movie_link.movie_id = movie_info.movie_id AND info_type.id = movie_info_idx.info_type_id;