SELECT MIN(name.name_pcode_cf), MIN(movie_companies.company_id), MIN(company_name.country_code), MIN(aka_name.name), MIN(name.id), MIN(kind_type.kind), MIN(cast_info.person_id), MIN(company_name.id), MIN(keyword.keyword) FROM kind_type, company_name, movie_companies, aka_name, cast_info, name, keyword WHERE name.name_pcode_cf IS NOT NULL AND company_name.country_code IS NOT NULL AND kind_type.kind = 'tv movie' AND aka_name.name IS NOT NULL AND keyword.keyword IS NULL AND company_name.id = movie_companies.company_id AND name.id = cast_info.person_id;