SELECT MIN(person_info.person_id), MIN(name.gender), MIN(title.id), MIN(movie_info.movie_id), MIN(title.production_year), MIN(movie_companies.movie_id), MIN(name.id), MIN(cast_info.person_id) FROM movie_companies, movie_info, person_info, cast_info, name, title WHERE name.gender != 'f' AND title.production_year BETWEEN 1908 AND 1957 AND movie_info.movie_id = title.id AND movie_companies.movie_id = title.id AND name.id = person_info.person_id AND name.id = cast_info.person_id;