SELECT MIN(movie_info.info), MIN(title.id), MIN(movie_info.movie_id), MIN(movie_info.info_type_id), MIN(movie_keyword.movie_id), MIN(info_type.id), MIN(role_type.role) FROM movie_info, role_type, movie_keyword, info_type, title WHERE movie_info.info = 'When Matt buys Patt a new face for their anniversary, Patt reluctantly tries it on for size. But at a disastrous dinner with friends it becomes painfully evident to everyone that when it comes to faces, one size definitely does not fit all.' AND role_type.role = 'cinematographer' AND movie_keyword.movie_id = movie_info.movie_id AND movie_info.movie_id = title.id AND info_type.id = movie_info.info_type_id;