SELECT MIN(comp_cast_type.kind), MIN(title.kind_id), MIN(title.id), MIN(movie_info.movie_id), MIN(title.production_year), MIN(movie_link.movie_id), MIN(movie_companies.movie_id), MIN(role_type.role), MIN(kind_type.id) FROM movie_link, kind_type, movie_companies, movie_info, role_type, comp_cast_type, title WHERE role_type.role != 'composer' AND title.production_year BETWEEN 1953 AND 2006 AND comp_cast_type.kind != 'cast' AND movie_companies.movie_id = title.id AND movie_info.movie_id = title.id AND title.id = movie_link.movie_id AND kind_type.id = title.kind_id;