SELECT MIN(cast_info.person_id), MIN(name.gender), MIN(title.episode_nr), MIN(name.id), MIN(title.id), MIN(title.production_year), MIN(movie_link.linked_movie_id), MIN(complete_cast.movie_id), MIN(kind_type.kind) FROM name, title, cast_info, kind_type, movie_link, complete_cast WHERE name.gender IS NOT NULL AND title.episode_nr <= 18556 AND title.production_year > 2002 AND kind_type.kind IS NULL AND title.id = complete_cast.movie_id AND title.id = movie_link.linked_movie_id AND name.id = cast_info.person_id;