SELECT MIN(cast_info.note), MIN(title.kind_id), MIN(aka_title.movie_id), MIN(title.title), MIN(movie_companies.movie_id), MIN(movie_info.note), MIN(kind_type.id), MIN(movie_info.movie_id) FROM movie_companies, movie_info, title, cast_info, kind_type, aka_title WHERE cast_info.note IS NOT NULL AND title.title IS NOT NULL AND movie_info.note != '(New York City, New York)' AND kind_type.id = title.kind_id AND movie_info.movie_id = aka_title.movie_id AND movie_info.movie_id = movie_companies.movie_id;