SELECT MIN(cast_info.person_id), MIN(title.kind_id), MIN(person_info.person_id), MIN(aka_name.person_id), MIN(name.id), MIN(name.name_pcode_cf), MIN(kind_type.id), MIN(kind_type.kind) FROM name, title, aka_name, cast_info, kind_type, person_info WHERE name.name_pcode_cf = 'A5362' AND kind_type.kind IN ('movie', 'tv movie', 'tv series', 'episode', 'tv mini series', 'video movie', 'video game') AND name.id = person_info.person_id AND kind_type.id = title.kind_id AND name.id = aka_name.person_id AND name.id = cast_info.person_id;