SELECT MIN(movie_info_idx.info), MIN(cast_info.person_id), MIN(role_type.role), MIN(aka_name.person_id), MIN(movie_companies.movie_id), MIN(name.id), MIN(movie_info_idx.movie_id), MIN(movie_link.linked_movie_id), MIN(aka_name.name), MIN(company_name.country_code) FROM movie_companies, name, aka_name, cast_info, movie_link, movie_info_idx, role_type, company_name WHERE company_name.country_code IN ('[it]', '[mn]', '[ca]', '[us]', '[fr]', '[bg]', '[bm]', '[ve]', '[kr]', '[gb]', '[es]', '[jp]', '[au]', '[na]', '[gl]', '[th]', '[in]', '[de]', '[ee]', '[ki]') AND aka_name.name = 'credits, Hironori Kanno in English' AND role_type.role = 'actress' AND movie_info_idx.info IS NULL AND name.id = aka_name.person_id AND movie_link.linked_movie_id = movie_info_idx.movie_id AND movie_info_idx.movie_id = movie_companies.movie_id AND cast_info.person_id = aka_name.person_id;