SELECT MIN(cast_info.person_id), MIN(company_name.name), MIN(aka_name.person_id), MIN(name.id), MIN(company_name.id), MIN(aka_name.name), MIN(movie_companies.company_id) FROM name, movie_companies, aka_name, cast_info, company_name WHERE aka_name.name = 'credits, Hironori Kanno in English' AND company_name.name IS NOT NULL AND cast_info.person_id = aka_name.person_id AND company_name.id = movie_companies.company_id AND name.id = aka_name.person_id;