SELECT MIN(cast_info.note), MIN(movie_info_idx.movie_id), MIN(link_type.link), MIN(movie_link.linked_movie_id), MIN(cast_info.movie_id), MIN(company_name.country_code), MIN(kind_type.kind) FROM link_type, cast_info, kind_type, movie_link, movie_info_idx, company_name WHERE kind_type.kind != 'tv movie' AND cast_info.note != '(producer) (as Koh Shibusawa)' AND link_type.link != 'spin off' AND company_name.country_code != '[es]' AND cast_info.movie_id = movie_info_idx.movie_id AND cast_info.movie_id = movie_link.linked_movie_id;